        private static double GetTreeNum(string[] input, int xInc, int yInc)
        {
            double treeNum = 0;
            int currX = 0, currY = 0;
            for (int i = 0; currY < input.Length; i++)
            {
                currY += yInc;
                currX += xInc;
                if (currX > 30)
                {
                    currX -= 31;
                }
                if (currY >= input.Length)
                {
                    continue;
                }

                if (input[currY][currX] == '#')
                {
                    treeNum++;
                }
            }

            return treeNum;
        }
